insert into T_MASDATACONDITIONPRICE values(8,1,'ZVPE','Valor perfil','Valor del perfil. En el caso de peticiones por acto médico,  será igual al valor de la condición ZPR0.En caso de que la petición esté asociada a un contrato,  esta condición obtendrá un valor que represente el peso del perfil en el conjunto de la petición en base al valor de la condición ZPR0.',NOW(),1,0,NOW(),NULL,NULL);
insert into T_MASDATACONDITIONPRICE values(9,1,'ZPER','Reguladora de Perfil','Con esta condición obtenemos el descuento que ha sufrido el perfil al estar sujeto a contrato en comparación a su precio de venta individual. Equivaldría restar el valor de la condición ZPR0 al valor de la condición ZVPE.',NOW(),1,0,NOW(),NULL,NULL);
insert into T_MASDATACONDITIONPRICE values(10,1,'ZFIP','Valor Fijo Petición','Representa el valor del contrato fijo por petición que le corresponde a la prueba en base a su peso representado por el valor de su condición ZPR0.',NOW(),1,0,NOW(),NULL,NULL);
insert into T_MASDATACONDITIONPRICE values(11,1,'ZFIR','Regul.Fijo Petición','Con esta condición obtenemos el descuento que ha sufrido la prueba al estar sujeta a contrato en comparación a su precio de venta individual. Equivaldría restar el valor de la condición ZPR0 al valor de la condición ZFIP.',NOW(),1,0,NOW(),NULL,NULL);
insert into T_MASDATACONDITIONPRICE values(12,1,'ZCAP','Valor Capitativo','Representa el valor del contrato capitativo fijo que le corresponde a la prueba en base a su peso representado por el valor de su condición ZPR0.',NOW(),1,0,NOW(),NULL,NULL);
insert into T_MASDATACONDITIONPRICE values(13,1,'ZCAR','Reguladora Capitativ','Con esta condición obtenemos el descuento que ha sufrido la prueba al estar sujeta a contrato capitativo fijo en comparación a su precio de venta individual. Equivaldría restar el valor de la condición ZPR0 al valor de la condición ZCAP.',NOW(),1,0,NOW(),NULL,NULL);
insert into T_MASDATACONDITIONPRICE values(14,1,'ZPR1','Precio neto','Representa el precio neto total de la posición. Será el precio que posteriormente será transferido a finanzas.',NOW(),1,0,NOW(),NULL,NULL);
insert into T_MASDATACONDITIONPRICE values(15,1,'HM00','Precio neto (manual)','Precio neto manual que el usuario podrá añadir para modificar el precio de una posición. Este precio prevalecería sobre cualquier otro determinado con anterioridad.',NOW(),1,0,NOW(),NULL,NULL);
